  public function __construct($username, $password) {
    $this->username = $username;
    $this->password = $password;
    $this->curl = curl_init();
    $this->loginPostData = $this->buildLoginPostData();
  }

  private function buildLoginPostData() {
    $username = urlencode($this->username);
    $password = urlencode($this->password);
    return "__EVENTTARGET=&__EVENTARGUMENT=&__VSTATE=eJz7z8ifws%252fKZWhsamBhYWBgYsmfIsaUhkKIMDHyizHJsYdlFmcm5aRmpDAxA%252fnyDEAGK0gNUBokzxKSWlGSmpLCxI4QlGcECXCiC3CjC%252fDCDORHlxGEyQjzQ1kpALbbHB0%253d&__VIEWSTATE=&txtUserName=$username&txtPassword=$password&btnSubmit=%D0%92%D0%BB%D0%B5%D0%B7&__EVENTVALIDATION=%2FwEWBAL%2BraDpAgKl1bKzCQK1qbSRCwLCi9reA3ZPH%2F0OXuiqks41bB%2BF30DwDzP9";
  }

  private function isLogged() {
    return eregi('Справки', $this->loginOutput);
  }

  public function isCampaignStarted() {
    $started = eregi('зборни', $this->loginOutput);
    return $started;
  }

  private function post($requestData, $url, $logged = true) {
    $content;
    curl_setopt($this->curl, CURLOPT_URL, $url);
    curl_setopt($this->curl, CURLOPT_POST, true);
    curl_setopt($this->curl, CURLOPT_POSTFIELDS, $requestData);
    if (!$logged) {
      curl_setopt($this->curl, CURLOPT_COOKIEJAR, self::COOKIES_PATH);
    }
    curl_setopt($this->curl, CURLOPT_COOKIEFILE, self::COOKIES_PATH);
    curl_setopt($this->curl, CURLOPT_COOKIESESSION, true);
    curl_setopt($this->curl, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($this->curl, CURLOPT_FOLLOWLOCATION, true);
    curl_setopt($this->curl, CURLOPT_FRESH_CONNECT, true);
    curl_setopt($this->curl, CURLOPT_USERAGENT, 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/535.7 (KHTML, like Gecko) Chrome/16.0.912.75 Safari/535.7');
    $content = curl_exec($this->curl);
    return $content;
  }

  public function login() {
    $this->loginOutput = $this->post($this->loginPostData, self::LOGIN_URL, false);
    return $this->isLogged();
  }

  private function getSubscribeButtonId($id) {
    return str_replace('btnElectiveDisciplineName', 'btnSubscribe', $id);
  }

  private function getDisciplineStatus($dom, $id) {
    $status = $dom->getElementById($this->getSubscribeButtonId($id));
    if ($status) {
      return true;
    }
    return false;
  }

  private function parsePageDiscipline($dom, $discipline, $page) {
    $currentId = $discipline->getAttribute('id');
    $formValidation = urlencode($dom->getElementById('__EVENTVALIDATION')->getAttribute('value'));
    $vstate = urlencode($dom->getElementById('__VSTATE')->getAttribute('value'));
    if (eregi('btnElectiveDisciplineName', $currentId)) {
      $status = $this->getDisciplineStatus($dom, $discipline->getAttribute('id'));
      $this->disciplines[trim($discipline->nodeValue)] = array('page' => $page, 'id' => $this->getSubscribeButtonId($currentId), 'status' => $status);
    }
  }

  private function parsePageDisciplines($pageContent, $page) {
    $disciplines;
    $dom = new DOMDocument();
    $dom->loadHTML($pageContent);
    $links = $dom->getElementsByTagName('a');
    $id = 'rptElectiveDisciplines_';
    foreach ($links as $key => $link) {
      $this->parsePageDiscipline($dom, $link, $page);
    }
  }

  private function collectElectiveDisciplines() {
    foreach ($this->pages as $key => $page) {
      $output = $this->post($page, self::INNER_URL);
      $this->parsePageDisciplines($output, $key);
    }
  }

  private function addDisciplineByPage($id, $page) {
