  private function addDisciplineHelper($discipline) {
    if (!$this->disciplines) {
      $this->collectElectiveDisciplines();
    }
    foreach ($this->disciplines as $key => $value) {
      if ($discipline === $key && $value['status']) {
        return $this->addDisciplineByPage($value['id'], $value['page']);
      }
    }
  }

  private function disciplineExists($discipline) {
    if (!$this->disciplines) {
      $this->collectElectiveDisciplines();
    }
    return !!$this->disciplines[$discipline];
  }

  public function addDiscipline($discipline) {
    if (!$this->isLogged()) {
      throw new Exception('You\'re not logged into the system!');
    } else if (!$this->isCampaignStarted()) {
      throw new Exception('The campaign is not started!');
    } else if (!$this->disciplineExists($discipline)) {
      throw new Exception('The discipline does not exists!');
    }
    return $this->addDisciplineHelper($discipline);
  }

